'       DEALER_CODE,',
'       DEALER_NAME,',
'       RMA_ORG_CODE,',
'       (SELECT HL.LOCATION_CODE',
'          FROM apps.HR_ORGANIZATION_UNITS_V A,',
'               HR_LOCATIONS HL,',
'               ORG_ORGANIZATION_DEFINITIONS OOD',
'         WHERE     A.ORGANIZATION_ID = INVENTORY_ORGANIZATION_ID',
'               AND A.LOCATION_ID = HL.LOCATION_ID',
'               AND OOD.ORGANIZATION_ID = A.ORGANIZATION_ID',
'               AND OOD.ORGANIZATION_CODE = RMA_ORG_CODE)',
'          RMA_ORG_LOCATION,',
'       RMA_NUMBER,',
'       (SELECT DISTINCT TO_CHAR (RMA_DATE, ''DD-Mon-YYYY'')',
'          FROM ITC.LGM_RMA_TRASACTION_DETAILS a',
'         WHERE a.RMA_NUMBER = RMA_NUMBER and GRR_NUMBER = GRR_NO AND ROWNUM=1)',
'          RMA_DATE,',
'       RMA_MODEL_CODE,',
'      -- RMA_SERIAL_NUMBER,',
'       GRR_NO,',
'       (SELECT DISTINCT TO_CHAR (grr_date, ''DD-Mon-YYYY'')',
'          FROM ITC.LGM_RMA_TRASACTION_DETAILS',
'         WHERE GRR_NUMBER = GRR_NO and rownum=1)',
'          GRR_DATE,',
'		  TO_CHAR(TO_DATE(MATERIAL_RECEIVED_DATE,''MM/DD/YYYY''),''DD-Mon-YYYY'') MATERIAL_RECEIVED_DATE,',
'       --TO_char (MATERIAL_RECEIVED_DATE, ''DD-MON-YYYY'') MATERIAL_RECEIVED_DATE,',
'       FOC_DEPOT_CODE,',
'       FOC_DEPOT_NAME,',
'       (SELECT HL.LOCATION_CODE',
'          FROM apps.HR_ORGANIZATION_UNITS_V A,',
'               HR_LOCATIONS HL,',
'               ORG_ORGANIZATION_DEFINITIONS OOD',
'         WHERE     A.ORGANIZATION_ID = INVENTORY_ORGANIZATION_ID',
'               AND A.LOCATION_ID = HL.LOCATION_ID',
'               AND OOD.ORGANIZATION_ID = A.ORGANIZATION_ID',
'               AND OOD.ORGANIZATION_CODE = FOC_DEPOT_CODE)',
'          FOC_DEPOT_LOC,',
'       FOC_MODEL_CODE,',
'      -- FOC_SERIAL_NUMBER,',
'	  DATE_OF_FAILURE,',
'      --TO_char (DATE_OF_FAILURE, ''DD-MON-YYYY'') DATE_OF_FAILURE,',
'       FOC_WARRANTY_TYPE,',
'       FOC_ORDER_NUMBER,',
'       TO_CHAR (FOC_ORD_DATE,''DD-Mon-YYYY'') FOC_ORDER_CREATION,',
'       FOC_INVOICE_NUMBER,',
'       TO_CHAR (FOC_INV_DATE, ''DD-Mon-YYYY'') FOC_INV_DATE,',
'       FOC_DELIVERY_NO,',
'       (SELECT DISTINCT (QUANTITY_RECEIVED)',
'          FROM ITC.LGM_RMA_TRASACTION_DETAILS',
'         WHERE item_code = RMA_MODEL_CODE AND RMA_NUMBER = RMA_NUMBER and rownum=1)',
'          QUANTITY_RECEIVED,',
'       (SELECT SUM (ORDERED_QUANTITY)',
'          FROM oe_order_headers_all ooha, oe_order_lines_all oola',
'         WHERE     ooha.header_id = oola.header_id',
'               AND ooha.order_number = FOC_order_number',
'               AND ORDERED_ITEM = FOC_MODEL_CODE)',
'          ORDERED_QUANTITY',
'  FROM ITC.LGM_FOC_RMA_TRACKING_DETAILS',
' WHERE     DECODE (FOC_TYPE,',
'                   ''Dealer FOC'', ''FOC Disptches'',',
'                   ''Vendor Dispatches'') =',
'              NVL (',
'                 :P6_FOC_TYPE,',
'                 DECODE (FOC_TYPE,',
'                         ''Dealer FOC'', ''FOC Disptches'',',
'                         ''Vendor Dispatches''))',
'       AND DEALER_CODE = NVL (:P6_DEALER_CODE, DEALER_CODE)',
