,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Validate data to be entered'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'   l_error_msg        VARCHAR2 (4000);',
'   l_date             DATE;',
'   l_check            VARCHAR2 (100);',
'   l_check_no         VARCHAR2 (100);',
'   lv_SERIAL_NUMBER   NUMBER;',
'   lv_ITEM_CODE       VARCHAR2 (240);',
'   lv_GRR_NUMBER      NUMBER;',
'   lv_RMA_NUMBER      NUMBER;',
'   lv_RMA_DEPOT_CODE   VARCHAR2 (240);',
'   cursor c_upload_c',
'    is',
'        select seq_id,',
'			C001 FOC_DEPOT_CODE,',
'			C002 FOC_DEPOT_NAME,',
'			C003 DEALER_CODE,',
'			C004 DEALER_NAME,',
'			C005 FOC_ORDER_NUMBER,',
'			C006 FOC_MODEL_CODE,',
'			C007 FOC_SERIAL_NUMBER,',
'			C008 FOC_TYPE,',
'			C009 FOC_WARRANTY_TYPE,',
'			C010 REFERENCE_RMA,',
'			C011 RMA_ORG_CODE,',
'			C012 RMA_NUMBER,',
'			C013 GRR_NO,',
'			C014 RMA_MODEL_CODE,',
'			C015 RMA_SERIAL_NUMBER,',
'			C016 DATE_OF_FAILURE,',
'			C017 MATERIAL_RECEIVED_DATE,',
'			C018 COMPLAINT_NUMBER,',
'			C019 NFA_NUMBER,',
'			C020 RMA_REMARK,',
'			C021 CREATED_BY,',
'			C022 CREATION_DATE,',
'			C023 LAST_UPDATED_BY,',
'			C024 LAST_UPDATE_DATE,',
'			C025 ERROR_MSG',
'		from APEX_COLLECTIONS',
'        where collection_name =''LGM_FOC_RMA_LOAD'';',
'c_final VARCHAR(4000) := '''';',
'foc_col_check NUMBER := 0;',
'begin',
'',
'    for c in c_upload_c',
'    loop',
'    l_error_msg := null;',
'    l_date := null;',
'    l_check_no := null;',
'',
'    c_final := c_final || ''DOF: '' ||c.DATE_OF_FAILURE || '', ''|| ''MRD: ''||c.MATERIAL_RECEIVED_DATE; --c.FOC_DEPOT_CODE||'',''||c.FOC_DEPOT_NAME||'',''||c.DEALER_CODE||'',''||c.DEALER_NAME||'',''||c.FOC_ORDER_NUMBER||'',''||c.FOC_MODEL_CODE||'',''||c.FOC_SERIAL_NU'
||'MBER||'',''||c.FOC_TYPE||'',''||c.FOC_WARRANTY_TYPE||'',''||c.REFERENCE_RMA||'',''||c.RMA_ORG_CODE||'',''||c.RMA_NUMBER||'',''||c.GRR_NO||'',''||c.RMA_MODEL_CODE||'',''||c.RMA_SERIAL_NUMBER||'',''||c.DATE_OF_FAILURE||'',''||c.MATERIAL_RECEIVED_DATE||'',''||c.COMPLAINT_NUM'
||'BER||'',''||c.NFA_NUMBER||'',''||c.RMA_REMARK||'',''||c.CREATED_BY||'',''||c.CREATION_DATE||'',''||c.LAST_UPDATED_BY||'',''||c.LAST_UPDATE_DATE||'',''||c.ERROR_MSG;',
'    BEGIN ',
'    SELECT 1',
'    INTO foc_col_check',
'    FROM LGM_FOC_RMA_TRACKING_DETAILS',
'    WHERE c.FOC_WARRANTY_TYPE||c.FOC_TYPE||c.FOC_SERIAL_NUMBER||c.FOC_MODEL_CODE||c.FOC_ORDER_NUMBER||c.DEALER_NAME||c.DEALER_CODE||c.FOC_DEPOT_NAME|| c.FOC_DEPOT_CODE',
'            = FOC_WARRANTY_TYPE||FOC_TYPE||FOC_SERIAL_NUMBER||FOC_MODEL_CODE||FOC_ORDER_NUMBER||DEALER_NAME||DEALER_CODE||FOC_DEPOT_NAME||FOC_DEPOT_CODE',
'    ;',
'    EXCEPTION',
'        WHEN OTHERS THEN',
'        l_error_msg := l_error_msg || ''Incorrect FOC details, '';',
'    END;',
'',
'    IF c.REFERENCE_RMA NOT IN (''Y'',''N'')',
'    THEN',
'        --RAISE_APPLICATION_ERROR(-20000,''REF_RMA: ''||c.REFERENCE_RMA);',
'        l_error_msg := l_error_msg || ''Incorrect Reference RMA, '';',
'    END IF;',
'    IF l_error_msg IS NULL THEN',
'      IF c.REFERENCE_RMA = ''Y'' AND c.FOC_TYPE = ''Vendor FOC''',
'      THEN',
'         BEGIN',
'            SELECT DISTINCT SERIAL_NUMBER',
'              INTO lv_SERIAL_NUMBER',
'              FROM LGM_RMA_TRASACTION_DETAILS',
'             WHERE     RMA_NUMBER = c.RMA_NUMBER',
'                   AND ITEM_CODE = c.RMA_MODEL_CODE',
'                   AND SERIAL_NUMBER = c.RMA_SERIAL_NUMBER',
'                   AND GRR_NUMBER = c.GRR_NO;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               l_error_msg := l_error_msg || ''Invalid RMA Serial Number, '';',
'         END;',
'      END IF;',
'',
'',
'      IF c.REFERENCE_RMA = ''Y'' AND c.FOC_TYPE = ''Dealer FOC''',
'      THEN',
'         BEGIN',
'            SELECT DISTINCT SERIAL_NUMBER',
'              INTO lv_SERIAL_NUMBER',
'              FROM LGM_RMA_TRASACTION_DETAILS',
'             WHERE     RMA_NUMBER = c.RMA_NUMBER',
'                   AND ITEM_CODE = c.RMA_MODEL_CODE',
'                   AND SERIAL_NUMBER = c.RMA_SERIAL_NUMBER',
'                   AND GRR_NUMBER = c.GRR_NO',
'                   --   AND RMA_DEPOT_CODE = c.RMA_ORG_CODE;',
'                   AND RMA_DEALER_CODE = c.DEALER_CODE;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               l_error_msg := l_error_msg || ''Invalid RMA Dealer Code/Serial Number, '';',
'         END;',
'      END IF;',
'',
'',
'      IF c.REFERENCE_RMA = ''Y''',
'      THEN',
'         BEGIN',
'            SELECT DISTINCT ITEM_CODE',
'              INTO lv_ITEM_CODE',
'              FROM LGM_RMA_TRASACTION_DETAILS',
'             WHERE     RMA_NUMBER = c.RMA_NUMBER',
'                   AND ITEM_CODE = c.RMA_MODEL_CODE',
'                   AND GRR_NUMBER = c.GRR_NO',
'                   AND RMA_DEPOT_CODE = c.RMA_ORG_CODE;',
'         -- AND RMA_DEALER_CODE = c.DEALER_CODE;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               l_error_msg := l_error_msg || ''Invalid RMA Item Code, '';',
'         END;',
'      END IF;',
'',
'      IF c.REFERENCE_RMA = ''N''',
'      THEN',
'         BEGIN',
'            SELECT DISTINCT SERIAL_NUMBER',
'              INTO lv_SERIAL_NUMBER',
'              FROM mtl_serial_numbers msn, apps.mtl_system_items_kfv msi',
'             WHERE     msn.INVENTORY_ITEM_ID = msi.INVENTORY_ITEM_ID',
'                   AND msn.current_organization_id = msi.organization_id',
'                   AND msn.SERIAL_NUMBER = c.RMA_SERIAL_NUMBER',
'                   AND msi.CONCATENATED_SEGMENTS = c.RMA_MODEL_CODE;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               l_error_msg := l_error_msg || ''Invalid RMA Serial Number, '';',
'         END;',
'      END IF;',
'',
'      IF c.REFERENCE_RMA = ''N''',
'      THEN',
'         BEGIN',
'            SELECT CONCATENATED_SEGMENTS',
'              INTO lv_ITEM_CODE',
'              FROM ITC.LGM_FOC_RMA_SYSTEM_ITEMS_V',
'             WHERE CONCATENATED_SEGMENTS = c.RMA_MODEL_CODE;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               l_error_msg := l_error_msg || ''Invalid RMA Item Code 2, '';',
'         END;',
'      END IF;',
'',
'      IF c.REFERENCE_RMA = ''Y''',
'      THEN',
'         BEGIN',
'            SELECT DISTINCT GRR_NUMBER',
'              INTO lv_GRR_NUMBER',
'              FROM LGM_RMA_TRASACTION_DETAILS',
'             WHERE     RMA_NUMBER = c.RMA_NUMBER',
'                   AND ITEM_CODE = c.RMA_MODEL_CODE',
'                   AND GRR_NUMBER = c.GRR_NO',
'                   AND RMA_DEPOT_CODE = c.RMA_ORG_CODE;',
'         -- AND RMA_DEALER_CODE = c.DEALER_CODE;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               l_error_msg := l_error_msg || ''Invalid GRR number, '';',
'         END;',
'      END IF;',
'',
'      IF c.REFERENCE_RMA = ''Y'' AND c.FOC_TYPE = ''Dealer FOC''',
'      THEN',
'         BEGIN',
'            SELECT DISTINCT (a.RMA_NUMBER)',
'              INTO lv_RMA_NUMBER',
'              FROM ITC.LGM_RMA_TRASACTION_DETAILS a,',
'                   ITC.LGM_FOC_RMA_TRACKING_DETAILS b',
'             WHERE     1 = 1',
'                   AND a.RMA_NUMBER = c.RMA_NUMBER',
'                   AND a.RMA_DEPOT_CODE = c.RMA_ORG_CODE',
'                   AND a.RMA_DEALER_CODE = c.DEALER_CODE',
'                  -- AND a.RMA_NUMBER = b.RMA_NUMBER',
'                   AND b.FOC_TYPE = ''Dealer FOC''',
'                   AND B.REFERENCE_RMA = ''Y'';',
'         --AND B.RMA_SERIAL_NUMBER IS NOT NULL;',
'         --AND B.SEQ_NO = P_SR_NO;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               l_error_msg := l_error_msg || ''Invalid RMA Number for Dealer FOC, '';',
'         END;',
'      END IF;',
'',
'      IF c.REFERENCE_RMA = ''Y'' AND c.FOC_TYPE = ''Vendor FOC''',
'      THEN',
'         BEGIN',
'            SELECT DISTINCT (a.RMA_NUMBER)',
'              INTO lv_RMA_NUMBER',
'              FROM ITC.LGM_RMA_TRASACTION_DETAILS a,',
'                   ITC.LGM_FOC_RMA_TRACKING_DETAILS b',
'             WHERE     1 = 1',
'                   --  AND a.RMA_DEALER_CODE = c.DEALER_CODE',
'                   AND a.RMA_NUMBER = c.RMA_NUMBER',
'                   --AND a.RMA_NUMBER = b.RMA_NUMBER',
'                   AND b.FOC_TYPE = ''Vendor FOC''',
'                   AND B.REFERENCE_RMA = ''Y'';',
'         -- AND B.SEQ_NO = P_SR_NO;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               l_error_msg := l_error_msg || ''Invalid RMA Number for Vendor FOC, '';',
'         END;',
'      END IF;',
'',
'      IF c.REFERENCE_RMA = ''Y''                 --AND c.FOC_TYPE = ''Dealer FOC''',
'      THEN',
'         BEGIN',
'            SELECT DISTINCT a.RMA_DEPOT_CODE',
'              INTO lv_RMA_DEPOT_CODE',
'              FROM ITC.LGM_RMA_TRASACTION_DETAILS a,',
'                   ITC.LGM_FOC_RMA_TRACKING_DETAILS b',
'             WHERE     1 = 1',
'                   AND a.RMA_DEPOT_CODE IN (',
'                            SELECT ffv.flex_value',
'                             FROM fnd_flex_value_sets ffvs,',
'                                  fnd_flex_values ffv,',
'                                  fnd_flex_values_tl ffvt',
'                            WHERE 1 = 1 ',
'                                AND ffvs.flex_value_set_id = ffv.flex_value_set_id',
'                                AND ffv.flex_value_id = ffvt.flex_value_id',
'                                AND ffvt.language = USERENV (''LANG'')',
'                                AND ffv.enabled_flag = ''Y''',
'                                AND flex_value_set_name = ''LGM_AGRI_FOC_RMA_ORG''',
'                        )',
'                    AND A.RMA_DEPOT_CODE = c.RMA_ORG_CODE',
'                    AND a.RMA_NUMBER = b.RMA_NUMBER',
'                    -- AND b.FOC_TYPE = ''Dealer FOC''',
'                    AND B.REFERENCE_RMA = ''Y'';',
'         --AND B.SEQ_NO = P_SR_NO;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               l_error_msg := l_error_msg || ''Invalid RMA Org Code, '';',
'         END;',
'      END IF;',
'',
'      IF c.REFERENCE_RMA = ''N''',
'      THEN',
'         BEGIN',
'            SELECT DISTINCT (a.RMA_DEPOT_CODE)',
'              INTO lv_RMA_DEPOT_CODE',
'              FROM ITC.LGM_RMA_TRASACTION_DETAILS a,',
'                   ITC.LGM_FOC_RMA_TRACKING_DETAILS b',
'             WHERE     1 = 1',
'                   AND a.RMA_DEPOT_CODE IN',
'                          (SELECT ffv.flex_value',
'                             FROM fnd_flex_value_sets ffvs,',
'                                  fnd_flex_values ffv,',
'                                  fnd_flex_values_tl ffvt',
'                            WHERE     ffvs.flex_value_set_id =',
'                                         ffv.flex_value_set_id',
'                                  AND ffv.flex_value_id = ffvt.flex_value_id',
'                                  AND ffvt.language = USERENV (''LANG'')',
'                                  AND ffv.enabled_flag = ''Y''',
'                                  AND flex_value_set_name =',
'                                         ''LGM_AGRI_FOC_RMA_ORG''',
'                                  AND RMA_DEPOT_CODE = TRIM(c.RMA_ORG_CODE))',
'                   AND a.RMA_NUMBER = b.RMA_NUMBER',
'                   AND B.REFERENCE_RMA = ''N'';',
'         -- AND B.SEQ_NO = P_SR_NO;',
'         EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'               l_error_msg := l_error_msg || ''Invalid RMA Org Code, '';',
'         END;',
'      END IF;',
'    END IF;',
'            if l_error_msg is not null then',
'                apex_collection.update_member_attribute (',
'               p_collection_name   => ''LGM_FOC_RMA_LOAD'',',
'               p_seq               => c.seq_id,',
'               p_attr_number       => 25,',
'               p_attr_value        => l_error_msg);',
'            END IF;',
'        c_final := c_final || chr(13);',
'    END LOOP;',
'    --RAISE_APPLICATION_ERROR(-20001, c_final);',
'',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(777266770920998666)
,p_internal_uid=>777269698995998676
);
end;
